import { useEffect, useState } from 'react';
import { RadialChart } from 'react-vis';

export default function DB(props) {
  const [dbState, setDBState] = useState({});

  useEffect(()=>{
    fetch("http://localhost:4000/api/monitor/db").then(res => res.json())
    .then(db => {
      console.log(db)
      setDBState(db)
    })
  },[])
  return (
    <div className={`card-wrapper ${props.className}`}>
      <p className="card-title">DB</p>
      {dbState && 
        <div>
          <p>{dbState.name}</p>
          <p>Machine Memory Used</p>
          <RadialChart data={[
            {angle: dbState.machine_memory_used},
            {angle: dbState.machine_memory_total - dbState.machine_memory_used}]}
            width={150} 
            height={150} />
        </div>
      }
    </div>
  )
}
