export var SIGN_OUT = "SIGN_OUT";
export var FAILED_ADMIN_PASSWORD = "FAILED_ADMIN_PASSWORD";
export var SUCCEEDED_ADMIN_PASSWORD = "SUCCEEDED_ADMIN_PASSWORD";
export function sign_out() {
  return {
    type: SIGN_OUT,
    receivedAt: Date.now()
  };
}

function receive_account_results(res) {
  console.log(res.status);

  if (res.status === 200) {
    return {
      type: SUCCEEDED_ADMIN_PASSWORD,
      receivedAt: Date.now()
    };
  }

  return {
    type: FAILED_ADMIN_PASSWORD,
    receivedAt: Date.now()
  };
}

export function check_account(id, password) {
  var data = {
    login_id: id,
    login_password: password
  };
  return function (dispatch) {
    var url = process.env.NEXT_PUBLIC_USER_URL + "login";
    fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    }).then(function (res) {
      return dispatch(receive_account_results(res));
    });
  };
}
