import { useEffect, useState } from 'react';
import { ResponsiveContainer, PieChart, Pie } from 'recharts';

export default function DB(props) {
  const [dbState, setDBState] = useState({});

  useEffect(()=>{
    fetch("http://localhost:4000/api/monitor/db").then(res => res.json())
    .then(db => {
      console.log(db)
      setDBState(db)
    })
  },[])
  return (
    <div className={`card-wrapper ${props.className}`}>
      <p className="card-title">DB</p>
      {dbState && 
        <div>
          <p>{dbState.name}</p>
          <p>Machine Memory Used</p>
          <ResponsiveContainer width={200} height={200}>
            <PieChart width={730} height={250}>
              <Pie 
                data={[
                  {name: "used", memory: dbState.machine_memory_used}, 
                  {name: "free", memory: dbState.machine_memory_used}]} 
                dataKey="value" nameKey="name" 
                cx="50%" cy="50%" 
                outerRadius={50} fill="#8884d8" />
            </PieChart>
          </ResponsiveContainer>
        </div>
      }
    </div>
  )
}
