import { useEffect, useState } from 'react';
import { ResponsiveContainer, PieChart, Pie } from 'recharts';
import '../../styles/Admin/db.css'

export default function DB(props) {
  const [dbState, setDBState] = useState({});

  useEffect(()=>{
    fetch("http://localhost:4000/api/monitor/db").then(res => res.json())
    .then(db => {
      console.log(db)
      setDBState(db)
    })
  },[])
  return (
    <div className={`card-wrapper ${props.className}`}>
      <p className="card-title">DB - {dbState.name}</p>
      {dbState && 
        <div>
          <ResponsiveContainer width="100%" height={200}>
            <PieChart>
              <Pie 
                data={[
                  {name: "used", memory: dbState.machine_memory_used}, 
                  {name: "free", memory: dbState.machine_memory_total - dbState.machine_memory_used}]} 
                dataKey="memory" nameKey="name" 
                cx="50%" cy="50%" 
                outerRadius={60} fill="#ffa600" label />
            </PieChart>
          </ResponsiveContainer>
          <p className="memory-used-label">Machine Memory Used</p>
        </div>
      }
    </div>
  )
}
